    }
    
    /*
     * function to update bank
     */
    function update_bank($BankID,$params)
    {
        $this->db->where('BankID',$BankID);
        return $this->db->update('banks',$params);
    }
    
    /*
     * function to delete bank
     */
    function delete_bank($BankID)
    {
        return $this->db->delete('banks',array('BankID'=>$BankID));
    }
