    /*
     * Get good by GoodID
     */
    function get_good($GoodID)
    {
        return $this->db->get_where('goods',array('GoodID'=>$GoodID))->row_array();
    }
    
    /*
     * Get all goods count
     */
    function get_all_goods_count()
    {
        $this->db->from('goods');
        return $this->db->count_all_results();
    }
        
    /*
     * Get all goods
     */
    function get_all_goods($params = array())
    {
        $this->db->order_by('GoodID', 'desc');
        if(isset($params) && !empty($params))
        {
            $this->db->limit($params['limit'], $params['offset']);
        }
        return $this->db->get('goods')->result_array();
    }
        
