     */
    function add_good($params)
    {
        $this->db->insert('goods',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update good
     */
    function update_good($GoodID,$params)
    {
        $this->db->where('GoodID',$GoodID);
        return $this->db->update('goods',$params);
    }
    
    /*
     * function to delete good
     */
    function delete_good($GoodID)
    {
        return $this->db->delete('goods',array('GoodID'=>$GoodID));
